
public class SwitchSample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=2;
		switch(a)
		{
		case 1:
			System.out.println("One");
			break;
		case 2 :
			System.out.println("two");
			break;
			default :
				System.out.println("Not a Valid number");
				
		}
	}

}
